//custom theme
const theme: MPTheme = {
  /**COLORS */
  darkPink: "#ED5E9D",
  pink: "#F337DB1",
  mid: "#DDE2E8",
  grey: "#B9B9B9",
  green: "#08C455",
  white: "#FFFFFF",

  /**SPACING */
  // TODO: update to rem values
  spacingXxs: "4px",
  spacingXs: "8px",
  spacingS: "12px",
  spacingBase: "16px",
  spacingM: "24px",
  spacingL: "28px",
  spacingXL: "32px",

  /**FONT SIZE */
  fontSizeSmall: "14px",
  fontSizeBase: "16px",

  /**FONT WEIGHT */
  fontWeightBase: "400",
  fontWeightBold: "700",
};

export default theme;

