import React, { FC, useEffect, useState } from "react";
import { Card } from "../Card";
import { Button } from "../Button";
import axios from "axios";
import {Link} from ""
type CardListProps = {
  children?: React.ReactNode;
  className?: string;
};

export const CardList: FC<CardListProps> = ({ children, className }) => {
  const [data, setData] = useState<[]>([]);

  useEffect(() => {
    axios
      .get("https://moonpig.github.io/tech-test-frontend/search.json")
      .then((response) => {
        setData(response.data.Products);
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
      });
  }, [data]); // Empty dependency array means this useEffect runs once when the component mounts

  return (
    <div className="container">
      {data.map((product) => (
        <div key={product.ProductId} className="col-12 col-md-6 col-lg-3">
          <Card>
            <Card.Image
              src={product.ProductImage.Link.Href}
              alt={product.Title}
            />
            <Card.Footer>
            <Link to={`/card/${product.ProductId}`}>

              <Button onClick={() => setSelectedCard(product)} fullWidth>See card details </Button>
              </Link>
            </Card.Footer>
          </Card>
        </div>
      ))}
    </div>
  );
};

export default CardList;
